let cart={};function addToCart(a,b){if(b<=0)throw new Error("Количество должно быть положительным");cart[a]?cart[a]+=b:cart[a]=b;renderCart()}function renderCart(){const a=document.getElementById("cart-items");a.innerHTML="";for(let b in cart){const c=document.createElement("li");c.textContent=`Товар ${b} — Количество: ${cart[b]}`,a.appendChild(c)}}document.querySelectorAll(".add-btn").forEach(a=>{a.addEventListener("click",()=>{const b=a.parentElement.getAttribute("data-id");addToCart(b,1)})});
